import "./App.css";
import React, {useState} from "react";
import {useForm} from "react-hook-form";

let sheep = require('./data.json');
const render_products = (SheepHerd) => {
  return (
    <div className="category-section fixed">
      <h2 className="text-3xl font-extrabold tracking-tight text-gray-600 category-title">
        Sheep ({SheepHerd.length})
      </h2>
      <div
        className="m-6 p-3 mt-10 ml-0 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-6 xl:gap-x-10"
        style={{ maxHeight: "800px", overflowY: "scroll" }}
      >
        {/* Loop Products */}
        {SheepHerd.map((sheep, index) => {
          return (
            <div key={index} className="group relative shadow-lg">
              <div className=" min-h-80 bg-gray-200 aspect-w-1 aspect-h-1 rounded-md overflow-hidden group-hover:opacity-75 lg:h-60 lg:aspect-none">
                <img
                  src={sheep.image}
                  alt={sheep.name}
                  className="w-full h-full object-center object-cover lg:w-full lg:h-full"
                />
              </div>
              <div className="flex justify-between p-3">
                <div>
                  <h3 className="text-sm text-gray-700">
                    <a href={sheep.href}>
                      <span aria-hidden="true" className="absolute inset-0" />
                      <span style={{ fontSize: "16px", fontWeight: "600" }}>
                        {sheep.name}
                      </span>
                    </a>
                    <p>{sheep.breed}</p>
                  </h3>
                  <p className="mt-1 text-sm text-gray-500">
                    Weighs {sheep.weight}
                  </p>
                </div>
                <p className="text-sm font-medium text-green-600">
                  ${sheep.price}
                </p>
              </div>
              <div class="col">
          <button
            type="button"
            variant="light"
            onClick={() => removeFromCart(el)}
          >
            {" "}
            -{" "}
          </button>{" "}
          <button type="button" variant="light" onClick={() => addToCart(el)}>
            {" "}
            +{" "}
          </button>
        </div>
        <div class="col">
          ${el.price} <span class="close">&#10005;</span>
          {howManyofThis(el.id)}
        </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};


const App = () => {
    const [SheepHerd] = useState(sheep);
    const { register, handleSubmit, formState: { errors } } = useForm();
    const [dataF,setDataF] = useState({});
    const [viewer,setViewer] = useState(0);


    function Shop(){
      return (
        <div className="flex fixed flex-row">
          <div
            className="h-screen bg-slate-800 p-3 xl:basis-1/5"
            style={{ minWidth: "65%" }}
          >
            <div className="px-6 py-4">
              <h1 className="text-3xl mb-2 font-bold text-white">
                {" "}
                Sheep Auction{" "}
              </h1>
              <p className="text-gray-700 text-white">
                by -{" "}
                <b style={{ color: "White" }}>
                  Blake Clabaugh, Zach Schmitz
                </b>
              </p>
              <div className="py-10">

              </div>
            </div>
          </div>
          <div className="ml-5 p-10 xl:basis-4/5">
            {console.log(
              "Before render :",
              sheep.length,
              SheepHerd.length
            )}
            {render_products(SheepHerd)}
          </div>
        </div>
      );

    }
    function Payment(){
      const onSubmit = data => {
        console.log( data );
        console.log( data.fullName );
        // update hooks
        setDataF( data );
        setViewer( 1 );
      }
      return (
        <div>
          <form onSubmit={handleSubmit(onSubmit)} className="container mt-5">
            <div className="form-group">
              <input
                {...register("fullName", { required: true })}
                placeholder="Full Name"
                className="form-control"
              />
              {errors.fullName && (
                <p className="text-danger">Full Name is required.</p>
              )}
            </div>
            <div className="form-group">
              <input
                {...register("email", { required: true })}
                placeholder="Email"
                className="form-control"
              />
              {errors.email && (
                <p className="text-danger">Email is required.</p>
              )}
            </div>
            <div className="form-group">
              <input
                {...register("creditCard", { required: true })}
                placeholder="Credit Card"
                className="form-control"
              />
              {errors.creditCard && (
                <p className="text-danger">Credit Card is required.</p>
              )}
            </div>
            <div className="form-group">
              <input
                {...register("address", { required: true })}
                placeholder="Address"
                className="form-control"
              />
              {errors.address && (
                <p className="text-danger">Address is required.</p>
              )}
            </div>
            <div className="form-group">
              <input
                {...register("address")}
                placeholder="Address"
                className="form-control"
              />
            </div>
            <div className="form-group">
              <input
                {...register("city", { required: true })}
                placeholder="City"
                className="form-control"
              />
              {errors.city && (
                <p className="text-danger">City is required.</p>
              )}
            </div>
            <div className="form-group">
              <input
                {...register("state", { required: true })}
                placeholder="State"
                className="form-control"
              />
              {errors.address && (
                <p className="text-danger">State is required.</p>
              )}
            </div>
            <div className="form-group">
              <input
                {...register("zip", { required: true })}
                placeholder="Zip"
                className="form-control"
              />
              {errors.zip && (
                <p className="text-danger">Zip is required.</p>
              )}
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
        </div>
      );
    };

    function Summary(){
      const updateHooks = () => {
        setViewer( 0 );
        setDataF({});
      }
      return (<div>
                <h1>Payment summary:</h1>
                <h3>{dataF.fullName}</h3>
                <p>{dataF.email}</p>
                <p>{dataF.creditCard}</p>
                <p>{dataF.address}</p>
                <p>{dataF.address2}</p>
                <p>{dataF.city},{dataF.state} {dataF.zip} </p>
                <button onClick={updateHooks}className="btn btn-secondary">Submit</button>
              </div>);
      };

    return(
      <div>
        {viewer === 0 && <Shop />}
        {viewer === 1 && <Payment />}
        {viewer === 2 && <Summary />}
      </div>
    )

}; 
